const add = (n1, n2) => {
  //reverse()
  const a1 = n1.split('')
  const a2 = n2.split('')
  let flag = 0
  let sum = []
  while (a1.length || a2.length || flag) {
    let p1 = ~~a1.pop(1)
    let p2 = ~~a2.pop(1)
    sum.unshift((p1 + p2 + flag) % 10)
    flag = p1 + p2 + flag >= 10 ? 1 : 0
  }

  let s = sum.join('')
  return sum.join('')
}
var multiply = function (num1, num2) {
  console.time()
  if (num1 === '0' || num2 === '0') return "0"
  const a1 = num1.split('')
  const a2 = num2.split('')
  let arr = []
  let sum = ''
  let flag = 0
  let innerFlag = 0

  for (let i = a2.length - 1; i >= 0; i--) {
    for (let j = a1.length - 1; j >= 0; j--) {

      sum = "" + (((~~a2[i] * ~~a1[j]) % 10 + flag) % 10) + sum
      innerFlag = (~~a2[i] * ~~a1[j]) % 10 + flag >= 10 ? 1 : 0
      flag = Math.floor(~~a2[i] * ~~a1[j] / 10) + innerFlag
    }
    if (flag > 0) sum = flag + '' + sum
    arr.push(sum + Array(a2.length - i - 1).fill('0').join(''))
    sum = ''
    flag = 0
  }
  return arr.reduce(add)

};
