var multiply1 = function (num1, num2) {
  console.time()
  let ret = new Array(num1.length + num2.length).fill(0);
  for (let i = 0; i < num1.length; i++) {
    for (let j = 0; j < num2.length; j++) {
      //x‰∏∫‰∏™‰ΩçÔºåy‰∏∫ÂçÅ‰Ωç
      let x = Number(num1[i]) * Number(num2[j]);
      let y;
      if (x < 10) {
        y = 0;
      } else {
        let temp = x;
        x = x % 10;
        y = (temp - x) / 10;
      }
      //ÊâæÂà∞ÂØπÂ∫î‰ΩçÔºåÁõ∏Âä†
      ret[i + j] = ret[i + j] + y;
      ret[i + j + 1] = ret[i + j + 1] + x;
    }
  }
  //ËÄÉËôëÊØè‰∏™‰Ωç‰∏äÁöÑÊª°ÂçÅËøõ‰Ωç
  for (let i = ret.length - 1; i > 0; i--) {
    if (ret[i] > 9) {
      let temp = ret[i];
      ret[i] = ret[i] % 10;
      ret[i - 1] = ret[i - 1] + (temp - ret[i]) / 10;
    }
  }
  //Êé®Âá∫ÂâçÂØºÈõ∂
  while (ret[0] === 0) ret.shift();
  console.log("üöÄ ~ file: 43.js ~ line 116 ~ multiply ~ ret.length ? ret.join('')", ret.join(''))
  console.timeEnd()
  return ret.length ? ret.join('') : '0';
};


